INSERT INTO `recipe` VALUES (12,'Falafel (croquettes de pois chiches)','{\"1\": \"Faites tremper les pois chiches et les fèves dans l\'eau 12 h, les égoutter et les cuire 45 mn à l\'auto cuiseur.\", \"2\": \"Peler oignon et ail, les hacher ainsi que le persil.\", \"3\": \"Passer les fèves et les pois chiches au mixer (ou robot).\", \"4\": \"Mélanger avec le persil, l\'oignon, l\'ail, la farine, les épices, le sel.\", \"5\": \"Pétrissez le tout avec vos mains en ajoutant un peu d\'eau si nécessaire. Rassemblez la pâte et laisser reposer au réfrigérateur pendant minimum 30 mn.\", \"6\": \"Façonner une trentaine de boulettes de la grosseur d\'une pièce de 2 euros.\", \"7\": \"Les faire frire 2/3 mn puis les égoutter sur du papier absorbant.\", \"8\": \"Servir chaud ou froid avec des petites sauces tomates aux herbes, ou sauces yaourts.\"}','2023-09-01 19:49:55','facile',60,80,'{\"fat\": 15.1, \"fiber\": 0, \"sugar\": 3.3, \"energy\": 283, \"proteins\": 11.3}',4,'falafel.jpg',8),(13,'Sauce au poivre toute simple','{\"1\": \"Faire un roux avec le beurre et la farine.\", \"2\": \"Ajouter 1/4 l d\'eau en une seule fois et bien mélanger.\", \"3\": \"Ajouter le cube de bouillon, une cuillerée à soupe de vinaigre à l\'estragon et une cuillerée à café de poivre mignonette.\", \"4\": \"Faire épaissir. Ajouter du sel si nécessaire (inutile en principe, à cause du bouillon).\", \"5\": \"Au moment de servir, ajouter du poivre moulu : non, ça ne fait pas double emploi avec le poivre mignonette !\", \"6\": \"Allez-y molo quand même...\"}','2023-09-02 15:12:41','facile',15,30,'{\"fat\": 8.2, \"fiber\": 0, \"sugar\": 0.5, \"energy\": 76, \"proteins\": 0.2}',3,'pepper_sauce.jpg',9),(14,'Cabillaud aux olives et au vin blanc','{\"1\": \"Mettre dans un plat les filets de poisson.\", \"2\": \"Emincer les champignons et en recouvrir le poisson.\", \"3\": \"Ajouter les olives,le vin, le jus du citron, les herbes de Provence et les épices (rester dans des épices douces pour ne pas contrarier le goût des olives). Saler et poivrer.\", \"4\": \"Recouvrir le plat de papier d\'aluminium et enfourner.\", \"5\": \"Cuire jusqu\'à ce que la chair du poisson se détache facilement (1h si poisson surgelé).\"}','2023-09-02 15:41:25','facile',10,40,'{\"fat\": 14, \"fiber\": 5, \"sugar\": 7, \"energy\": 330, \"proteins\": 11.3}',4,'cabillaud.webp',10),(15,'Flan de courgettes simple','{\"1\": \"Faire cuire les courgettes sans enlever la peau.\", \"2\": \"Battre les six oeufs en omelette.\", \"3\": \"Mixer les courgettes cuites. Mélanger les courgettes, les oeufs et la crème fraîche.\", \"4\": \"Mettre dans des ramequins, après les avoir enduis de beurre (environ 12 ramequins).\", \"5\": \"Faire cuire 30 mn au four thermostat 6 (180°C).\"}','2023-09-02 16:01:59','facile',30,90,'{\"fat\": 5.65, \"fiber\": 1.4, \"sugar\": 9.7, \"energy\": 108, \"proteins\": 5.35}',1,'flan_courgette.webp',11),(19,'Tarte aux aubergines, tomates et parmesan','{\"1\": \"La pâte : mélanger rapidement avec la paume de la main la farine, le sel et l\'huile avec le beurre ramolli en pommade. Ajouter un trait d\'eau, pour lier et rassembler la pâte en boule (si elle est trop collante, rectifier en ajoutant de la farine), et laisser reposer 1 h si possible.\", \"2\": \"Garniture : étaler la pâte dans un plat fariné, saupoudrer le fond de tarte de parmesan râpé, puis étaler la moitié des aubergines (même sorties du congélateur à l\'instant!). \", \"3\": \"Parsemer de la moitié des tomates et de mozzarella, saler, poivrer, et répéter une fois l\'opération. \", \"4\": \"Terminer avec un filet d\'huile sur l\'ensemble, puis du parmesan.\", \"5\": \"Enfourner pendant 20 min à thermostat 6/7 (200°C), et servir tiède ou froide.\"}','2023-09-14 21:57:43','Facile',20,40,'{\"fat\": 17.29, \"fiber\": 1.95, \"sugar\": 22.22, \"energy\": 282.25, \"proteins\": 7.36}',6,'Tarte aux aubergines, tomates et parmesan-4802d5f5-06b4-4059-b97f-e59e52fa2a9f.webp',12),(20,'Chakchouka','{\"1\": \"Couper finement les oignons, les faire revenir dans une sauteuse avec l\'huile d\'olive. Couper en tranches très fines les poivrons, les rajouter dans la sauteuse. Couper en tranches les tomates et les mettre aussi dans la sauteuse. Salez, poivrez. Couvrir. Faîtes mijoter le tout. Rajouter un peu d\'huile d\'olive durant la cuisson si vous le souhaitez.\", \"2\": \"Dans une autre poêle, faites revenir vos merguez. Une fois cuites, coupez vos mergues en 4, les mettre avec les légumes. Laissez mijoter à feu doux, histoire de parfumer vos légumes du jus des merguez... (c\'est pour cela qu\'il n\'est pas nécessaire d\'utiliser un piment dans la recette). \", \"3\": \"Une fois le tout cuit, rajouter la coriandre. Cela ressemble à une ratatouille ou piperade. Vous pouvez rajouter sur le tout un oeuf par personne... \", \"4\": \"C\'est délicieux ! Bon appétit !\"}','2023-09-15 08:37:32','Facile',20,65,'{\"fat\": 122.41, \"fiber\": 3.51, \"sugar\": 17.15, \"energy\": 1423.81, \"proteins\": 62.09}',1,'Chakchouka-d9386594-c0e5-4fab-8f68-f76a99aab5b3.webp',7),(21,'Omelette','{\"1\": \"Battez les oeufs à la fourchette, salez et poivrez.\", \"2\": \"Faites chauffer le beurre, versez-en un peu dans les oeufs et mélangez. Versez les oeufs dans la poêle à feu vif, baissez le feu et laissez cuire doucement en ramenant les bords de l\'omelette au centre au fur et à mesure qu\'ils prennent.\", \"3\": \"Secouez un peu la poêle pour éviter que l\'omelette n\'attache, vérifiez la texture baveuse ou bien prise.\"}','2023-09-15 09:41:09','Facile',5,10,'{\"fat\": 10.13, \"fiber\": 0, \"sugar\": 0.58, \"energy\": 118.88, \"proteins\": 6.41}',4,'Omelette-8c532bf4-30ff-4ab6-a037-2a06d6ec94ec.jpg',8),(24,'Soupe aux légumes','{\"1\": \"Lavez vos légumes, épluchez-les et coupez-les grossièrement.\", \"2\": \"Dans une grande casserole ou une marmite, faites chauffer 2 cuillères à soupe d\'huile d\'olive et 10 g de beurre salé.\", \"3\": \"Faites revenir votre oignon pendant quelques minutes, puis ajoutez progressivement les légumes. Faites revenir 5 à 10 min à feu doux. Salez, poivrez et ajouter la pincée d\'herbes de Provence.\", \"4\": \"Recouvrez les légumes avec de l\'eau, couvrez et laissez cuire une bonne heure.\", \"5\": \"Mixez le tout et servez avec une petite cuillère à soupe de crème fraîche si vous le désirez.\"}','2023-09-19 18:37:14','Facile',25,90,'{\"fat\": 6.63, \"fiber\": 5.25, \"sugar\": 12.74, \"energy\": 132.53, \"proteins\": 2.24}',4,'soupeLegume-795e75de-f836-49f4-a7a4-c5fdf877693b.jpg',38),(25,'Crêpes','{\"1\": \"Mettez la farine dans un saladier avec le sel et le sucre.\", \"2\": \"Faites un puits au milieu et versez-y les œufs.\", \"3\": \"Commencez à mélanger doucement. Quand le mélange devient épais, ajoutez le lait froid petit à petit.\", \"4\": \"Quand tout le lait est mélangé, la pâte doit être assez fluide. Si elle vous paraît trop épaisse, rajoutez un peu de lait. Ajoutez ensuite le beurre fondu refroidi, mélangez bien.\", \"5\": \"Faites cuire les crêpes dans une poêle chaude (par précaution légèrement huilée si votre poêle à crêpes n\'est pas anti-adhésive). Versez une petite louche de pâte dans la poêle, faites un mouvement de rotation pour répartir la pâte sur toute la surface. Posez sur le feu et quand le tour de la crêpe se colore en roux clair, il est temps de la retourner.\", \"6\": \"Laissez cuire environ une minute de ce côté et la crêpe est prête.\", \"7\": \"Répétez jusqu\'à épuisement de la pâte.\"}','2023-09-20 16:38:54','Facile',10,25,'{\"fat\": 13.07, \"fiber\": 2.63, \"sugar\": 51.49, \"energy\": 348.18, \"proteins\": 9.87}',4,'crepes-d6a70614-acd0-4110-9e60-189786274d9c.jpg',37),(26,'Pizza maison','{\"1\": \"Préparer une pâte à pizza ou l\'acheter toute faite.\", \"2\": \"Étaler la pâte et la recouvrir d\'huile d\'olive.\", \"3\": \"Couper les tomates en rondelles, la mozzarella en cubes et le jambon en petits carrés. Éplucher l\'oignon et les champignons. Les hacher finement.\", \"4\": \"Recouvrir la pâte de sauce tomate, des tomates et de la mozzarella.\", \"5\": \"Disposer le jambon, les champignons et les oignons. Assaisonner d\'huile d\'olive, de sel, de poivre, d\'herbes de Provence, de thym, de persil...\", \"6\": \"Faire cuire au four à 200°C pendant environ 30 minutes jusqu\'à ce qu\'elle soit bien dorée.\"}','2023-09-20 16:49:27','Facile',15,45,'{\"fat\": 5.61, \"fiber\": 1.54, \"sugar\": 27.72, \"energy\": 185.44, \"proteins\": 6.74}',8,'pizza-d44f6545-c8cf-4d9d-acf5-88da983fb1d5.jpg',39),(27,'Tartare de saumon et d\'avocat','{\"1\": \"Coupez les avocats en deux, retirez le noyau et détaillez la chair en dés. Arrosez avec 1 cuillère à soupe de jus de citron, salez, poivrez et mélangez.\", \"2\": \"Détaillez les pavés de saumon en dés. Salez, poivrez, arrosez avec le reste de jus de citron et 1 cuillère à soupe d’huile d’olive.\", \"3\": \"A l’aide de cercles à pâtisserie, dressez les dés d’avocat dans chaque assiette. Recouvrez de saumon. Retirez les cercles, parsemez de pousses de cresson, arrosez du reste d’huile et servez frais.\"}','2023-10-02 11:04:14','Facile',15,15,'{\"fat\": 16.83, \"fiber\": 1.59, \"sugar\": 0.7, \"energy\": 218.05, \"proteins\": 14.99}',4,'salmon-tartare-with-avocado-mousse-d24506e1-f3c0-4d71-872d-c50514126906.jpg',9),(28,'Risotto aux champignons','{\"1\": \"Préparez les champignons. Passez-les rapidement sous un filet d\'eau, essuyez-les et détaillez-les en gros morceaux. Faites fondre une noix de beurre et faites-y revenir la gousse d\'ail émincée pendant 2 minutes. Ajoutez les champignons et laissez-les cuire environ 3 minutes en ajoutant le persil lavé et ciselé à la fin. Ajoutez 3 cuillères à soupe de vin blanc et laissez cuire 2 minutes jusqu\'à ce qu\'il réduise. Ajoutez la crème fraîche et mélangez. Salez, poivrez et réservez.\", \"2\": \"Faites bouillir un litre d\'eau dans lequel vous diluerez les cubes de bouillon. Dans une sauteuse, faites chauffer l\'huile d\'olive pour y faire blondir l\'oignon émincé pendant 3 minutes. Ajoutez le riz et laissez-le cuire en remuant pendant 2 minutes jusqu\'à ce qu\'il soit un peu transparent.\", \"3\": \"Ajoutez 10 cl de vin blanc (1/2 verre) et une fois qu\'il est absorbé par le riz, ajoutez une louche de bouillon, puis mélangez. Une fois que le bouillon est bien absorbé, ajoutez une nouvelle louche, et ainsi de suite jusqu\'à épuisement du bouillon (environ 20 bonnes minutes).\", \"4\": \"Ajoutez la moitié du parmesan et les champignons, attendez 2 minutes puis mélangez vivement.\", \"5\": \"Servez très chaud avec le reste du parmesan.\"}','2023-10-02 11:17:42','Facile',5,35,'{\"fat\": 9.98, \"fiber\": 2.01, \"sugar\": 17.83, \"energy\": 428.11, \"proteins\": 9.54}',5,'risotto-f83f6178-ae93-4541-8603-5b5b064afcf5.jpg',51),(29,'Feuilletés aux saucisses','{\"1\": \"Etaler la pâte feuilletée sur une épaisseur de 3 mm environ. Etaler de la moutarde avec le dos de la cuillère sur toute la pâte. Parsemer de fromage râpé (très légèrement).\", \"2\": \"Disposer une saucisse et découper un rectangle selon la hauteur de la saucisse permettant de l\'enrouler avec un léger surplus. Rouler la saucisse, bien fermer et couper en quatre.\", \"3\": \"Faire ainsi avec toutes les saucisses. Les diposer sur une feuille de papier cuisson.\", \"4\": \"Faire fondre le beurre (environ 30 secondes au micro-ondes) et graisser au pinceau chaque feuilleté. Enfourner pour 10 à 15 minutes, en surveillant la cuisson.\"}','2023-10-02 11:25:45','Facile',10,25,'{\"fat\": 41.57, \"fiber\": 1.3, \"sugar\": 36.46, \"energy\": 561.8, \"proteins\": 9.62}',4,'feuilletÃ©sSaucisses-b09c22ab-136f-44f8-83a6-a060217fdc6a.jpg',7),(30,'Burger maison','{\"1\": \"Préchauffer le four à 160°C. Poser les tranches de pain dans un plat, faces retournées. Étaler dessus la Savora et le ketchup. Déposer les tranches de fromage. Enfourner 5 minutes.\", \"2\": \"Cuire les steaks 5 minutes avec les oignons puis saler et poivrer.\", \"3\": \"Poser les oignons et la viande dans le pain, mettre à nouveau de la Savora et du ketchup à l\'intérieur. Refermer les pains.\", \"4\": \"Enfourner le tout pendant 10 minutes.\"}','2023-10-02 11:37:22','Facile',15,30,'{\"fat\": 25.59, \"fiber\": 9.22, \"sugar\": 84.31, \"energy\": 733.35, \"proteins\": 38.09}',2,'burger-66236d05-7eee-4ed2-9f58-f8e3f28623bc.jpg',52),(31,'Bièraubeurre','{\"1\": \"Commencez par faire un caramel à sec en versant le sucre dans une casserole à fond épais. Le caramel à sec s’obtient en faisant chauffer le caramel à feu vif. Laissez le sucre un moment sans le toucher. Quand les grains commencent à bouger ou fondre très légèrement, commencez à remuer avec une spatule en silicone résistante aux fortes températures. Ramenez le sucre non fondu dans le sucre liquide et remuez sans cesse, jusqu\'à obtention d’un caramel liquide encore clair (n’allez pas jusqu\'au caramel ambré à brun qui serait trop amer).\", \"2\": \"Versez alors la crème entière dans le caramel. Attention à éloigner les plus petits car le caramel a tendance à gicler quand on le déglace.\", \"3\": \"Ajoutez ensuite la bière (ou le cidre). Laissez revenir à ébullition et retirez du feu.\", \"4\": \"Servez dans des chopes de bière ou des verres Harry Potter et recouvrez de crème chantilly. Décorez, si vous le souhaitez, avec du caramel liquide et saupoudrez de cannelle. Régalez-vous !\"}','2023-10-02 12:01:47','Moyen',10,10,'{\"fat\": 0.39, \"fiber\": 0, \"sugar\": 40.62, \"energy\": 3.88, \"proteins\": 0.07}',4,'beer-a85f04d6-1bed-4b91-852d-3203c1bea906.jpg',53),(32,'Toast avocat, oeuf et chèvre','{\"1\": \"Faites griller le pain\", \"2\": \"Découpez les avocats en fines lamelles\", \"3\": \"Cassez les oeufs et battez-les dans un saladier. Ajoutez le chèvre écrasé grossièrement. Salez / poivrez\", \"4\": \"Faites chauffer l\'huile dans une poêle. Versez le mélange oeufs-chèvre et, à l\'aide d\'une cuillère en bois, remuez pour former les oeufs brouillés\", \"5\": \"Etalez les oeufs sur la tartine, rajoutez des lamelles d\'avocat et agrémentez de ciboulette ciselée \"}','2023-09-25 19:42:34','Facile',5,10,'{\"fat\": 22.64, \"fiber\": 2.92, \"sugar\": 13.98, \"energy\": 263.3, \"proteins\": 14.87}',4,'avocado-toast-0411c9a8-020c-42d6-9162-50d61d23c448.jpg',37),(33,'Banana bread','{\"1\": \"Préchauffez four à 180° ( thermostat 6 )\", \"2\": \"Ecrasez deux bananes à la fourchette, et coupez la troisième dans la longueur\", \"3\": \"Dans un saladier, mélangez farine et levure, puis ajoutez le beurre de cacahuète et la boisson végétale\", \"4\": \"Mélangez bien avant d\'ajouter les oeufs, puis les bananes écrasées. Remuez jusqu\'a avoir une pâte homogène\", \"5\": \"Versez la préparation dans un moule à cake beurré. Décorez avec la banane restante et laissez cuire 50min\"}','2023-09-25 19:57:32','Facile',5,60,'{\"fat\": 19.94, \"fiber\": 7.95, \"sugar\": 51.31, \"energy\": 314.32, \"proteins\": 19.77}',4,'bananaBread-9ae9ae84-cfbb-41b6-911c-3e597e4e26b7.jpg',37),(34,'Pudding de chia et fruits rouges','{\"1\": \"La veille, mélangez dans un saladier les graines de chia et la boisson\", \"2\": \"Répartissez dans 4 verines, remuez bien et placez au frigo\", \"3\": \"Le lendemain, mixez les fruits rouges ( gardez-en quelques-uns pour la déco ) jusqu\'a obtenir une consistance homogène\", \"4\": \"Versez le pudding de chia et ajoutez les fruits en topping\"}','2023-09-25 20:27:21','Facile',10,720,'{\"fat\": 3.82, \"fiber\": 6.83, \"sugar\": 6.45, \"energy\": 8.88, \"proteins\": 2.78}',4,'CHIA_FRUIT_ROUGE-c3e180ed-eedb-47c3-b2c2-2eb0891a3f31.webp',37),(35,'Salade printanière','{\"1\": \"Coupez les radis en rondelles après les avoir nettoyés\", \"2\": \"Préparez la vinaigrette\", \"3\": \"Faites cuire le quinoa. Lavez les asperges et coupez leurs extrémités. Saisissez-les une dizaine de minutes à la poêle\", \"4\": \"Préparez les oeufs. Mettez-les dans une casserole d\'eau froide et une fois l\'eau à ébullition, laissez-les 5min\", \"5\": \"Dressez chaque assiette selon votre bon vouloir, en coupant l\'oeuf caillé en deux. N\'oubliez pas la vinaigrette\"}','2023-09-25 20:34:07','Facile',15,35,'{\"fat\": 11.12, \"fiber\": 8.45, \"sugar\": 44.18, \"energy\": 370.55, \"proteins\": 18.76}',4,'salade-printaniere-2292-29493478-4c40-4bb1-bcf9-e19d22349c82.jpg',37),(36,'Tomates farcies au quinoa','{\"1\": \"Coupez le haut des tomates pour en faire un chapeau. Evidez-les et versez la chair dans un saladier. Faites cuire les tomates vides environ 10min dans l\'eau boullante\", \"2\": \"Pendant ce temps, faites cuire le quinoa environ 15min\", \"3\": \"Emincez l\'oignon et faites le revenir dans une poêle. Ajoutez les lanières de poivron et la chair des tomates. Laissez mijoter 10min en remuant régulièrement\", \"4\": \"Préchauffez le four à 160°. Hors du feu, ajoutez à la préparation de tomates l\'oeuf battu et le quinoa. Mélangez les tomates au four et garnissez-les de la préparation. Laissez cuire 15min. Mettez le reste dans le plat tout autour des tomates. Dégustez avec la salade lavée et essorée\"}','2023-09-25 21:05:16','Moyen',15,45,'{\"fat\": 4.23, \"fiber\": 5.76, \"sugar\": 27.92, \"energy\": 184.6, \"proteins\": 7.79}',4,'tomatesFarcies-8e7912b0-ede9-453b-b99e-0bef2fb95fae.webp',38),(37,'Compote de fruits et matcha latte ','{\"1\": \"Pelez et coupez en quartiers les fruits. Retirez les coeurs. Placez-les dans une casserole, puis ajoutez les grains de vanille.  \", \"2\": \"Couvrez et faites cuire à feu très doux durant un bon quart d\'heure en remuant de temps en temps\", \"3\": \"Dégustez tiède avec une poignée d\'oléagineux\", \"4\": \"Pour accompagner, préparez le matcha latte : versez dans une tasse la boisson végétale, ajoutez le thé matcha et fouettez vigoureusement jusqu\'a l\'obtention d\'une belle mousse. Sirotez ! \"}','2023-09-25 21:49:37','Facile',10,720,'{\"fat\": 0.35, \"fiber\": 4.77, \"sugar\": 26.56, \"energy\": 3.31, \"proteins\": 0.77}',4,'compote-c93e1600-188a-4759-9aad-e65381ced6b7.jpg',38),(38,'Barre de céréales','{\"1\": \"Toastez les flocons d\'avoine dans une poêle à feu doux durant une dizaine de minutes\", \"2\": \"Dénoyautez les dattes. Mixez-les avec les amandes et les noisettes jusqu\'a obtenir une pâte lisse. \", \"3\": \"Ajoutez au flocon d\'avoine les graines de sésame et les abricots hachés finement. Mélangez bien.\", \"4\": \"Aplatissez de façon uniforme la préparation sur une plaque de four tapissée de papier cuisson. Réservez au régrigérateur plusieurs heures\", \"5\": \"Coupez en barres et conservez au réfrigérateur dans une boîte hermétique pendant maximum une semaine\"}','2023-09-25 21:57:40','Facile',10,30,'{\"fat\": 27.65, \"fiber\": 10.18, \"sugar\": 36.39, \"energy\": 68.81, \"proteins\": 11.07}',4,'barreCereales-73d71a7a-689d-4a21-9d09-291ee212289a.jpg',38),(39,'Cookie tout choco','{\"1\": \"Mélangez tous les ingrédients à l\'exception du chocolat, de façon à obtenir une pâte collante. Laissez reposer au frais pendant 30min\", \"2\": \"Hachez grossièrement le chocolat pour former des pépites \", \"3\": \"Formez des cookies en prélevant une cuillerée à soupe de pâte ; roulez-la dans vos mains avant de l\'aplatir sur plaque de four recouverte de papier cuisson. Ajoutez les pépites de chocolat sur le dessus en les enfonçant légèrement\", \"4\": \"Enfournez pour 15 à 20min. Surveillez la cuisson : les cookies doivent être dorés, pas brûlés ! \"}','2023-09-25 22:04:36','Facile',15,40,'{\"fat\": 11.52, \"fiber\": 4.52, \"sugar\": 43.02, \"energy\": 226.03, \"proteins\": 5.92}',4,'cookie-093ed40d-cf63-484d-b031-91032ca560d2.webp',38),(40,'Pancakes légers','{\"1\": \"Pelez la banane et écrasez-la dans un saladier. Ajoutez l\'oeuf, la farine, les graines de la gousse et mélangez jusqu\'a obtenir une consistance homogène\", \"2\": \"Dans une grande poêle, faites chauffer l\'huile à feu moyen. Versez des louches de pâte pour former les pancakes\", \"3\": \"Faie cuire 2 à 3min de chaque côté\"}','2023-09-25 22:11:15','Facile',5,15,'{\"fat\": 3.16, \"fiber\": 3.14, \"sugar\": 27.57, \"energy\": 162.65, \"proteins\": 4.74}',4,'pancakes-b8ea2362-f6a6-4837-b5e5-6ae755e747af.jpg',39),(41,'Risotto primavera','{\"1\": \"Faites cuire les petits pois et les asperges pendant 10min dans une casserole d\'eau bouillante. Coupez les asperges en tronçon de 2cm et gardez les têtes pour le service. \", \"2\": \"Dans une casserole, faites revenir le riz pendant 2min  dans l\'huile d\'olive. Il doit être bien brillant\", \"3\": \"Ajoutez une louche de bouillon chaud. Attendez que le riz l\'ait absorbé et ajoutez les légumes. Versez progressivement le bouillon au fur et à mesure de son absorption par le riz. Remuez délicatement et goûtez, le riz doit être al dente. \", \"4\": \"Ajoutez le parmesan râpé. Remuez délicatement et laissez reposer à couvert 2min.\", \"5\": \"Servez dans les assiettes le riz crémeux et ajoutez les pointes d\'asperge, ainsi qu\'une pincée de fleur de sel\"}','2023-09-25 22:18:02','Facile',20,60,'{\"fat\": 13.76, \"fiber\": 6.78, \"sugar\": 107.82, \"energy\": 735.13, \"proteins\": 39.81}',4,'risottopARMESAN-1b59ffa3-4fc5-4ec7-bcb7-525cae5a366d.jpg',39),(42,'Ratatouille aux pois chiches','{\"1\": \"Faites cuire les pois chiches dans l\'eau bouillante pendant 2 à 3 heures en remuant régulièrement\", \"2\": \"Epluchez tous les légumes. Coupez les aubergines et les courgettes en morceaux de 2cm environ, les tomates en quartiers et les poivrons en lanières\", \"3\": \"Préparez la ratatouille : dans une casserole, faites revenir dans l\'huile d\'olive l\'oignon et l\'ail finement ciselé. Ajoutez les aubergines et les poivrons et faites cuire pendant 5min. Rajoutez ensuite les courgettes, les tomates et les herbes. Couvrez\", \"4\": \"Laissez mijoter une bonne demi heure ( plus c\'est cuit, meilleur c\'est ). Ajoutez à mi-cuisson les pois chiches, puis salez / poivrez\"}','2023-09-25 22:27:05','Moyen',30,180,'{\"fat\": 4.91, \"fiber\": 10.17, \"sugar\": 24.3, \"energy\": 244.11, \"proteins\": 4.7}',4,'RATatouille-752c54e0-f68b-4c13-afcf-0a307e39163f.jpg',39),(43,'Glace au chocolat','{\"1\": \"Faire fondre le chocolat et le beurre.\", \"2\": \"Battre les 3 jaunes d\'oeuf avec le sucre jusqu\'à ce que le mélange blanchisse.\", \"3\": \"Ajouter le chocolat fondu et mélanger.\", \"4\": \"Battre les blancs en neige puis les incorporer délicatement à la préparation.\", \"5\": \"Placer au congélateur environ 3 à 4 h, en fonction de votre gourmandise.\", \"6\": \"Bonne dégustation!\"}','2023-10-03 17:33:13','Facile',15,60,'{\"fat\": 22.61, \"fiber\": 1.85, \"sugar\": 23.51, \"energy\": 281.41, \"proteins\": 6.73}',4,'fresh-chocolate-dessert-indulgence-close-up-generated-by-ai-cd24acf1-eba3-463e-836e-d5653a6c193c.jpg',54);
